import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import "./BillingForm.css";

const BillingForm = () => {
  const navigate = useNavigate();
  const { id } = useParams(); // For updating an existing record

  const [billing, setBilling] = useState({
    patientName: "",
    phoneNumber: "",
    email: "",
    billingDate: "",
    totalAmount: "",
    services: [{ name: "", price: "" }],
  });

  const [error, setError] = useState("");

  useEffect(() => {
    if (id) {
      // Fetch existing billing data for editing
      axios
        .get(`http://localhost:8080/api/billing/${id}`)
        .then((res) => setBilling(res.data))
        .catch((err) => console.error("Error fetching billing:", err));
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBilling({ ...billing, [name]: value });
  };

  const handleServiceChange = (index, e) => {
    const { name, value } = e.target;
    const updatedServices = [...billing.services];
    updatedServices[index][name] = value;
    setBilling({ ...billing, services: updatedServices });
  };

  const addService = () => {
    setBilling({
      ...billing,
      services: [...billing.services, { name: "", price: "" }],
    });
  };

  const removeService = (index) => {
    const updatedServices = billing.services.filter((_, i) => i !== index);
    setBilling({ ...billing, services: updatedServices });
  };

  const validateForm = () => {
    if (!billing.patientName || !billing.phoneNumber || !billing.email) {
      setError("Please fill all required fields.");
      return false;
    }
    if (!/^\d{10}$/.test(billing.phoneNumber)) {
      setError("Phone number must be 10 digits.");
      return false;
    }
    if (!/\S+@\S+\.\S+/.test(billing.email)) {
      setError("Invalid email format.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      if (id) {
        // Update existing billing record
        await axios.put(`http://localhost:8080/api/billing/${id}`, billing);
      } else {
        // Create new billing record
        await axios.post("http://localhost:8080/api/billing", billing);
      }
      navigate("/");
    } catch (error) {
      console.error("Error saving billing:", error);
    }
  };

  return (
    <div className="billing-form">
      <h2>{id ? "Edit Billing" : "Add Billing"}</h2>
      {error && <p className="error">{error}</p>}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="patientName"
          placeholder="Patient Name"
          value={billing.patientName}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="phoneNumber"
          placeholder="Phone Number"
          value={billing.phoneNumber}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={billing.email}
          onChange={handleChange}
          required
        />
        <input
          type="datetime-local"
          name="billingDate"
          value={billing.billingDate}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="totalAmount"
          placeholder="Total Amount"
          value={billing.totalAmount}
          onChange={handleChange}
          required
        />

        <h3>Services</h3>
        {billing.services.map((service, index) => (
          <div key={index} className="service-row">
            <input
              type="text"
              name="name"
              placeholder="Service Name"
              value={service.name}
              onChange={(e) => handleServiceChange(index, e)}
              required
            />
            <input
              type="number"
              name="price"
              placeholder="Price"
              value={service.price}
              onChange={(e) => handleServiceChange(index, e)}
              required
            />
            <button type="button" onClick={() => removeService(index)}>
              Remove
            </button>
          </div>
        ))}
        <button type="button" onClick={addService}>
          Add Service
        </button>
        <br />
        <button type="submit">{id ? "Update" : "Submit"}</button>
      </form>
    </div>
  );
};

export default BillingForm;
