import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './BillingList.css';

const BillingList = () => {
    const [billingList, setBillingList] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8080/api/billing')
            .then(response => setBillingList(response.data))
            .catch(error => console.error('Error fetching billing:', error));
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/api/billing/${id}`);
            setBillingList(billingList.filter(b => b.id !== id));
        } catch (error) {
            console.error('Error deleting invoice:', error);
        }
    };

    return (
        <div className="billing-list-container">
            <h2>Invoices</h2>
            <Link to="/add" className="btn">Create Invoice</Link>

            <table>
                <thead>
                    <tr>
                        <th>Patient Name</th>
                        <th>Billing Date</th>
                        <th>Total Amount</th>
                        <th>Payment Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {billingList.map(billing => (
                        <tr key={billing.id}>
                            <td>{billing.patient_name}</td>
                            <td>{new Date(billing.billing_date).toLocaleString()}</td>
                            
                            {/* âœ… Prevent "undefined" error by checking if total_amount is valid */}
                            <td>
                                {billing.total_amount !== undefined 
                                    ? `$${billing.total_amount.toFixed(2)}`
                                    : 'N/A'}
                            </td>

                            <td>{billing.payment_status}</td>
                            <td>
                                <Link to={`/edit/${billing.id}`} className="btn">Edit</Link>
                                <button onClick={() => handleDelete(billing.id)} className="btn-delete">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default BillingList;
