import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8080/api', // Update with your backend URL
  headers: { 'Content-Type': 'application/json' }
});

// Medical Records API
export const MedicalRecordService = {
  create: (patientId, record) => api.post(`/patients/${patientId}/medical-records`, record),
  getAll: () => api.get('/medical-records'),
  getById: (id) => api.get(`/medical-records/${id}`)
};

// Patient API
export const PatientService = {
  getPatients: () => api.get('/patients'), // Adjust endpoint as necessary
  getById: (id) => api.get(`/patients/${id}`),
};
