import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AppointmentList = () => {
    const [appointments, setAppointments] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8080/api/appointments')
            .then((response) => {
                setAppointments(response.data);
            })
            .catch((error) => console.error('Error fetching appointments:', error));
    }, []);

    return (
        <div className="container mt-5">
            <h2>Appointments</h2>
            <Link to="/add-appointment" className="btn btn-primary mb-3">Add Appointment</Link>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Patient</th>
                        <th>Doctor</th>
                        <th>Date</th>
                        <th>Reason</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {appointments.map((appointment) => (
                        <tr key={appointment.id}>
                            <td>{appointment.id}</td>
                            <td>{appointment.patient.name}</td>
                            <td>{appointment.doctor.name}</td>
                            <td>{new Date(appointment.appointmentDate).toLocaleString()}</td>
                            <td>{appointment.reason}</td>
                            <td>{appointment.status}</td>
                            <td>
                                <Link to={`/edit-appointment/${appointment.id}`} className="btn btn-warning me-2">
                                    Edit
                                </Link>
                                <button
                                    className="btn btn-danger"
                                    onClick={() => {
                                        axios.delete(`http://localhost:8080/api/appointments/${appointment.id}`)
                                            .then(() => {
                                                setAppointments(appointments.filter((a) => a.id !== appointment.id));
                                            });
                                    }}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AppointmentList;
