import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './BillingList.css';

const BillingList = () => {
    const [billings, setBillings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBillings = async () => {
            try {
                const response = await fetch('http://localhost:8080/api/billings');  // ✅ Correct API Endpoint
                if (!response.ok) {
                    throw new Error(`Failed to fetch: ${response.status}`);
                }
                const data = await response.json();
                console.log('Fetched Billing Data:', data);
                setBillings(data);
            } catch (error) {
                console.error('Error fetching billings:', error);
                setError('Failed to fetch billing data.');
            } finally {
                setLoading(false);
            }
        };

        fetchBillings();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="billing-list-container">
            <h1>Invoice List</h1>

            <Link to="/add-invoice" className="btn">Create New Invoice</Link>

            <table>
                <thead>
                    <tr>
                        <th>Invoice ID</th>
                        <th>Patient Name</th>
                        <th>Billing Date</th>
                        <th>Total Amount</th>
                        <th>Payment Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {billings.map((billing) => (
                        <tr key={billing.id}>
                            <td>{billing.id}</td>
                            <td>{billing.patientName}</td> {/* ✅ Matching Field */}
                            <td>{new Date(billing.billingDate).toLocaleDateString()}</td> {/* ✅ Date Formatting */}
                            <td>${billing.totalAmount.toFixed(2)}</td> {/* ✅ Currency Formatting */}
                            <td>{billing.paymentStatus}</td>
                            <td>
                                <Link to={`/edit-invoice/${billing.id}`} className="btn">Edit</Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default BillingList;
