import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AppointmentForm = ({ onAppointmentAdded }) => {
    const [doctors, setDoctors] = useState([]);
    const [formData, setFormData] = useState({
        doctorId: '',
        phone: '',
        email: '',
        reason: ''
    });

    const [errors, setErrors] = useState({});

    useEffect(() => {
        const fetchDoctors = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/doctors');
                setDoctors(response.data);
            } catch (error) {
                console.error('Error fetching doctors:', error);
            }
        };
        fetchDoctors();
    }, []);

    const validateForm = () => {
        let isValid = true;
        const newErrors = {};

        if (!formData.doctorId) {
            newErrors.doctorId = 'Doctor is required';
            isValid = false;
        }
        if (!formData.phone.match(/^\d{10}$/)) {
            newErrors.phone = 'Phone number must be 10 digits';
            isValid = false;
        }
        if (!formData.email.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) {
            newErrors.email = 'Invalid email format';
            isValid = false;
        }
        if (formData.reason.trim() === '') {
            newErrors.reason = 'Reason is required';
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        try {
            await axios.post('http://localhost:8080/api/appointments', formData);
            alert('Appointment booked successfully!');
            onAppointmentAdded(); 
            setFormData({ doctorId: '', phone: '', email: '', reason: '' });
        } catch (error) {
            console.error('Error booking appointment:', error);
            alert('Failed to book appointment');
        }
    };

    return (
        <div className="p-6 bg-white rounded-lg shadow-md">
            <h2 className="text-xl font-bold mb-4">Book Appointment</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-4">
                    <label className="block mb-1">Doctor</label>
                    <select
                        name="doctorId"
                        value={formData.doctorId}
                        onChange={handleChange}
                        className="w-full p-2 border rounded"
                    >
                        <option value="">Select Doctor</option>
                        {doctors.map((doc) => (
                            <option key={doc.id} value={doc.id}>
                                {doc.name}
                            </option>
                        ))}
                    </select>
                    {errors.doctorId && <p className="text-red-500">{errors.doctorId}</p>}
                </div>

                <div className="mb-4">
                    <label className="block mb-1">Phone</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        className="w-full p-2 border rounded"
                    />
                    {errors.phone && <p className="text-red-500">{errors.phone}</p>}
                </div>

                <div className="mb-4">
                    <label className="block mb-1">Email</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className="w-full p-2 border rounded"
                    />
                    {errors.email && <p className="text-red-500">{errors.email}</p>}
                </div>

                <div className="mb-4">
                    <label className="block mb-1">Reason</label>
                    <textarea
                        name="reason"
                        value={formData.reason}
                        onChange={handleChange}
                        className="w-full p-2 border rounded"
                    />
                    {errors.reason && <p className="text-red-500">{errors.reason}</p>}
                </div>

                <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                    Book Appointment
                </button>
            </form>
        </div>
    );
};

export default AppointmentForm;
