import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './MedicalRecords.css';

const MedicalRecordsList = () => {
    const [records, setRecords] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchRecords = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/medical-records');
                setRecords(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching medical records:', error);
                setError('Failed to load records');
                setLoading(false);
            }
        };

        fetchRecords();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="records-container">
            <h2>Medical Records</h2>
            <Link to="/add-record" className="add-button">Add New Record</Link>
            
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Diagnosis</th>
                        <th>Treatment</th>
                        <th>Prescription</th>
                        <th>Patient ID</th>
                        <th>Created At</th>
                        <th>Updated At</th>
                    </tr>
                </thead>
                <tbody>
                    {records.map((record) => (
                        <tr key={record.id}>
                            <td>{record.id}</td>
                            <td>{record.diagnosis}</td>
                            <td>{record.treatment}</td>
                            <td>{record.prescription}</td>
                            <td>{record.patient?.id || 'N/A'}</td>
                            <td>{record.createdAt ? new Date(record.createdAt).toLocaleString() : 'N/A'}</td>
                            <td>{record.updatedAt ? new Date(record.updatedAt).toLocaleString() : 'N/A'}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default MedicalRecordsList;
