import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./BillingList.css";

const BillingList = () => {
  const [billings, setBillings] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/billing")
      .then((res) => setBillings(res.data))
      .catch((err) => console.error("Error fetching billing:", err));
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://localhost:8080/api/billing/${id}`).then(() => {
      setBillings(billings.filter((billing) => billing.id !== id));
    });
  };

  return (
    <div className="billing-list">
      <h2>Billing Records</h2>
      <Link to="/add">Add New Billing</Link>
      <ul>
        {billings.map((billing) => (
          <li key={billing.id}>
            <p>Patient: {billing.patientName}</p>
            <p>Total: â‚¹{billing.totalAmount}</p>
            <Link to={`/edit/${billing.id}`}>Edit</Link>
            <button onClick={() => handleDelete(billing.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BillingList;
