import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import jsPDF from 'jspdf';             // For PDF generation
import 'jspdf-autotable';
import './BillingList.css';

const BillingList = () => {
    const [billings, setBillings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // ✅ Fetch Billing Data
    useEffect(() => {
        const fetchBillings = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/billings');  // API endpoint
                setBillings(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching billing data:', error);
                setError('Failed to fetch billing data.');
                setLoading(false);
            }
        };
        fetchBillings();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    // ✅ PDF Download Function
    const downloadPDF = () => {
        const doc = new jsPDF();
        doc.text('Billing List', 15, 15);

        const tableData = billings.map(billing => ([
            billing.id,
            billing.patientName,
            new Date(billing.billingDate).toLocaleDateString(),
            `₹${billing.totalAmount.toFixed(2)}`,
            billing.paymentStatus
        ]));

        doc.autoTable({
            head: [['ID', 'Patient Name', 'Billing Date', 'Total Amount', 'Payment Status']],
            body: tableData,
            startY: 25
        });

        doc.save(`billing_list_${new Date().toISOString().split('T')[0]}.pdf`);
    };

    return (
        <div className="billing-container">
            <h2>Billing List</h2>

            <div className="btn-group">
                <Link to="/add-invoice" className="btn">Create New Invoice</Link>
                <button onClick={downloadPDF} className="btn">Download PDF</button>
            </div>

            <table className="billing-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Patient Name</th>
                        <th>Billing Date</th>
                        <th>Total Amount</th>
                        <th>Payment Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {billings.length > 0 ? (
                        billings.map((billing) => (
                            <tr key={billing.id}>
                                <td>{billing.id}</td>
                                <td>{billing.patientName}</td>
                                <td>{new Date(billing.billingDate).toLocaleDateString()}</td>
                                <td>₹{billing.totalAmount.toFixed(2)}</td>
                                <td>{billing.paymentStatus}</td>
                                <td>
                                    <Link to={`/edit-invoice/${billing.id}`} className="btn">Edit</Link>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6" style={{ textAlign: 'center' }}>No invoices found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default BillingList;
