import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './BillingList.css';

const BillingList = () => {
    const [billings, setBillings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBillings = async () => {
            try {
                const response = await fetch("http://localhost:8080/api/billings");  // Check API URL
    
                if (!response.ok) {
                    throw new Error(`Failed to fetch: ${response.status}`);
                }
    
                const data = await response.json();
                console.log("Fetched Billing Data:", data);  // Check if data is received
                setBillings(data);
            } catch (error) {
                console.error("Error fetching billings:", error);
                setError("Failed to fetch billing data.");
            } finally {
                setLoading(false);
            }
        };
    
        fetchBillings();
    }, []);
    
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div className="billing-list-container">
            <h1>Billing List</h1>
            <Link to="/add-invoice" className="btn">Create New Invoice</Link>
            
            <table className="billing-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Patient Name</th>
                        <th>Billing Date</th>
                        <th>Total Amount</th>
                        <th>Payment Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {billings.length > 0 ? (
                        billings.map(bill => (
                            <tr key={bill.id}>
                                <td>{bill.id}</td>
                                <td>{bill.patientName}</td>
                                <td>{new Date(bill.billingDate).toLocaleDateString()}</td>
                                <td>${bill.totalAmount.toFixed(2)}</td>
                                <td>{bill.paymentStatus}</td>
                                <td>
                                    <Link to={`/edit-invoice/${bill.id}`} className="btn">Edit</Link>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6">No billing records found.</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default BillingList;
