import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { MedicalRecordService } from '../services/api';

const MedicalRecordDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [record, setRecord] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadRecord = async () => {
      try {
        const response = await MedicalRecordService.getById(id);
        setRecord(response.data);
      } catch (error) {
        console.error('Error loading record:', error);
        alert('Medical record not found');
      } finally {
        setLoading(false);
      }
    };
    loadRecord();
  }, [id]);

  if (loading) return <div className="text-center">Loading record details...</div>;

  if (!record) return <div className="text-red-500 text-center">Medical Record not found!</div>;

  return (
    <div className="container mx-auto p-6">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h1 className="text-2xl font-bold mb-4 text-blue-600">{record.diagnosis}</h1>
        
        <div className="space-y-4 text-gray-700">
          <div>
            <span className="font-semibold">Patient:</span> 
            {record.patient?.firstName} {record.patient?.lastName}
          </div>
          
          <div>
            <span className="font-semibold">Date:</span> 
            {new Date(record.date).toLocaleDateString()}
          </div>

          <div>
            <span className="font-semibold">Treatment Plan:</span>
            <p className="mt-1 whitespace-pre-wrap">{record.treatment}</p>
          </div>

          {record.notes && (
            <div>
              <span className="font-semibold">Additional Notes:</span>
              <p className="mt-1 whitespace-pre-wrap">{record.notes}</p>
            </div>
          )}
        </div>

        {/* Button to go back to the medical records list */}
        <button 
          onClick={() => navigate('/medical-records')} 
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Back to Medical Records
        </button>
      </div>
    </div>
  );
};

export default MedicalRecordDetail;
