import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AppointmentList = () => {
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios.get('http://localhost:8080/api/appointments')
            .then(res => {
                setAppointments(res.data);
                setLoading(false);
            })
            .catch(err => {
                console.error('Error fetching appointments:', err);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div className="flex justify-center items-center h-screen text-xl">Loading...</div>;
    }

    return (
        <div className="p-8 max-w-6xl mx-auto bg-white shadow-lg rounded-lg">
            <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">Appointment List</h2>

            <div className="overflow-x-auto">
                <table className="w-full table-auto border-collapse">
                    <thead>
                        <tr className="bg-blue-500 text-white">
                            <th className="p-4 text-left">Doctor</th>
                            <th className="p-4 text-left">Phone</th>
                            <th className="p-4 text-left">Email</th>
                            <th className="p-4 text-left">Reason</th>
                            <th className="p-4 text-left">Appointment Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {appointments.length > 0 ? (
                            appointments.map((appointment) => (
                                <tr key={appointment.id} className="border-b hover:bg-gray-100 transition duration-200">
                                    <td className="p-4">{appointment.doctor?.name || 'N/A'}</td>
                                    <td className="p-4">{appointment.phone}</td>
                                    <td className="p-4">{appointment.email}</td>
                                    <td className="p-4">{appointment.reason}</td>
                                    <td className="p-4">
                                        {new Date(appointment.appointmentDate).toLocaleString()}
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="5" className="p-6 text-center text-gray-500">
                                    No appointments found.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default AppointmentList;
