import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './BillingList.css';

const BillingList = () => {
    const [billings, setBillings] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:8080/api/billing')
            .then(response => {
                setBillings(response.data);
            })
            .catch(error => console.error('Error fetching billing data:', error));
    }, []);

    return (
        <div className="billing-list-container">
            <h2>Billing List</h2>

            {/* âœ… Create Invoice Button */}
            <button 
                className="create-btn" 
                onClick={() => navigate('/billing/add')}
            >
                Create Invoice
            </button>

            <table className="billing-table">
                <thead>
                    <tr>
                        <th>Patient Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Amount</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {billings.map(billing => (
                        <tr key={billing.id}>
                            <td>{billing.patientName}</td>
                            <td>{billing.email}</td>
                            <td>{billing.phone}</td>
                            <td>${billing.totalAmount}</td>
                            <td>
                                <button 
                                    onClick={() => navigate(`/billing/edit/${billing.id}`)}
                                    className="edit-btn"
                                >
                                    Edit
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default BillingList;
