import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable";  // Import the autoTable plugin
import axios from "axios";
import "./BillingList.css"; // Add your styling

const BillingList = () => {
    const [billings, setBillings] = useState([]);

    // ✅ Fetch Billing Data from Backend
    useEffect(() => {
        axios.get("http://localhost:8080/api/billings")
            .then((response) => {
                console.log("Data fetched:", response.data);
                setBillings(response.data);
            })
            .catch((error) => console.error("Error:", error));
    }, []);

    // ✅ PDF Generation Function
    const downloadPDF = () => {
        const doc = new jsPDF();

        // Title
        doc.setFontSize(18);
        doc.text("Billing List", 14, 20);

        // ✅ Table Headers
        const tableColumn = ["ID", "Patient Name", "Billing Date", "Total Amount", "Payment Status"];

        // ✅ Table Rows with Data Formatting
        const tableRows = billings.map((billing) => [
            billing.id,
            billing.patientName,
            new Date(billing.billingDate).toLocaleDateString(),
            `$${billing.totalAmount.toFixed(2)}`,
            billing.paymentStatus
        ]);

        // ✅ Ensure Proper autoTable Usage
        doc.autoTable({
            head: [tableColumn],
            body: tableRows,
            startY: 30,  // Define start position
            theme: "grid",  
            styles: { fontSize: 10, cellPadding: 4 },
            margin: { top: 30 }
        });

        // ✅ Save PDF
        doc.save("Billing_List.pdf");
    };

    return (
        <div className="container">
            <h2>Billing List</h2>
            <button onClick={downloadPDF}>Download PDF</button>
            
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Patient Name</th>
                        <th>Billing Date</th>
                        <th>Total Amount</th>
                        <th>Payment Status</th>
                    </tr>
                </thead>
                <tbody>
                    {billings.map((billing) => (
                        <tr key={billing.id}>
                            <td>{billing.id}</td>
                            <td>{billing.patientName}</td>
                            <td>{new Date(billing.billingDate).toLocaleDateString()}</td>
                            <td>${billing.totalAmount.toFixed(2)}</td>
                            <td>{billing.paymentStatus}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default BillingList;
