import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAppointments, deleteAppointment } from '../services/AppointmentService';

const AppointmentList = () => {
    const [appointments, setAppointments] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        loadAppointments();
    }, []);

    const loadAppointments = async () => {
        try {
            const res = await getAppointments();
            setAppointments(res.data);
        } catch (error) {
            console.error('Failed to load appointments:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteAppointment(id);
            loadAppointments();
        } catch (error) {
            console.error('Error deleting appointment:', error);
        }
    };

    return (
        <div className="container">
            <h2>Appointment List</h2>
            <button onClick={() => navigate('/add-appointment')}>Add Appointment</button>
            <table>
                <thead>
                    <tr>
                        <th>Patient</th>
                        <th>Doctor</th>
                        <th>Date</th>
                        <th>Reason</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {appointments.map((app) => (
                        <tr key={app.id}>
                            <td>{app.patient.name}</td>
                            <td>{app.doctor.name}</td>
                            <td>{new Date(app.appointmentDate).toLocaleString()}</td>
                            <td>{app.reason}</td>
                            <td>{app.status}</td>
                            <td>
                                <button onClick={() => navigate(`/edit-appointment/${app.id}`)}>Edit</button>
                                <button onClick={() => handleDelete(app.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AppointmentList;
