import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './BillingForm.css';

const BillingForm = () => {
    const [billing, setBilling] = useState({
        patient_name: '',
        billing_date: '',
        total_amount: 0,
        payment_status: '',
        services: [{ service_name: '', price: 0, quantity: 1 }]
    });

    const navigate = useNavigate();
    const { id } = useParams();

    // ✅ Fetch existing billing record if editing
    useEffect(() => {
        if (id) {
            axios.get(`http://localhost:8080/api/billing/${id}`)
                .then(response => {
                    setBilling(response.data);
                })
                .catch(error => console.error('Error fetching billing:', error));
        }
    }, [id]);

    // ✅ Handle service field changes
    const handleServiceChange = (index, field, value) => {
        const updatedServices = [...billing.services];
        updatedServices[index][field] = value;

        // Automatically calculate total amount
        const newTotal = updatedServices.reduce((sum, service) => {
            return sum + (service.price * service.quantity);
        }, 0);

        setBilling({
            ...billing,
            services: updatedServices,
            total_amount: newTotal
        });
    };

    // ✅ Add new service row
    const addService = () => {
        setBilling({
            ...billing,
            services: [...billing.services, { service_name: '', price: 0, quantity: 1 }]
        });
    };

    // ✅ Remove service row
    const removeService = (index) => {
        const updatedServices = billing.services.filter((_, i) => i !== index);
        const newTotal = updatedServices.reduce((sum, service) => {
            return sum + (service.price * service.quantity);
        }, 0);

        setBilling({
            ...billing,
            services: updatedServices,
            total_amount: newTotal
        });
    };

    // ✅ Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        const endpoint = id
            ? `http://localhost:8080/api/billing/${id}`
            : `http://localhost:8080/api/billing`;

        const method = id ? 'put' : 'post';

        try {
            await axios[method](endpoint, billing);
            navigate('/billing');
        } catch (error) {
            console.error('Error saving billing:', error);
        }
    };

    return (
        <div className="billing-form-container">
            <h2>{id ? 'Edit Invoice' : 'Create Invoice'}</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Patient Name:</label>
                    <input
                        type="text"
                        value={billing.patient_name}
                        onChange={(e) => setBilling({ ...billing, patient_name: e.target.value })}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Billing Date:</label>
                    <input
                        type="datetime-local"
                        value={billing.billing_date}
                        onChange={(e) => setBilling({ ...billing, billing_date: e.target.value })}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Payment Status:</label>
                    <select
                        value={billing.payment_status}
                        onChange={(e) => setBilling({ ...billing, payment_status: e.target.value })}
                        required
                    >
                        <option value="">Select Status</option>
                        <option value="Pending">Pending</option>
                        <option value="Paid">Paid</option>
                        <option value="Cancelled">Cancelled</option>
                    </select>
                </div>

                <h3>Services</h3>
                {billing.services.map((service, index) => (
                    <div key={index} className="service-row">
                        <input
                            type="text"
                            placeholder="Service Name"
                            value={service.service_name}
                            onChange={(e) => handleServiceChange(index, 'service_name', e.target.value)}
                            required
                        />
                        <input
                            type="number"
                            placeholder="Price"
                            value={service.price}
                            onChange={(e) => handleServiceChange(index, 'price', parseFloat(e.target.value))}
                            required
                        />
                        <input
                            type="number"
                            placeholder="Quantity"
                            value={service.quantity}
                            onChange={(e) => handleServiceChange(index, 'quantity', parseInt(e.target.value))}
                            required
                        />
                        <button type="button" onClick={() => removeService(index)}>Remove</button>
                    </div>
                ))}

                <button type="button" onClick={addService}>Add Service</button>

                <div className="form-group">
                    <label>Total Amount:</label>
                    <input type="number" value={billing.total_amount} readOnly />
                </div>

                <button type="submit">{id ? 'Update Invoice' : 'Create Invoice'}</button>
            </form>
        </div>
    );
};

export default BillingForm;
