import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './BillingList.css';  // Ensure you have proper CSS for styling

const BillingList = () => {
    const [billings, setBillings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // ✅ Fetch billing data from backend API
    useEffect(() => {
        const fetchBillings = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/billing');  // ✅ Replace with your actual backend URL
                console.log('Billing Data:', response.data);  // For debugging
                setBillings(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching billing data:', error);
                setError('Failed to fetch billing data.');
                setLoading(false);
            }
        };
        fetchBillings();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="billing-container">
            <h2>Billing List</h2>
            <Link to="/add-invoice" className="btn">Create New Invoice</Link>

            {/* ✅ Display Billing Data */}
            <table className="billing-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Patient Name</th>
                        <th>Billing Date</th>
                        <th>Total Amount</th>
                        <th>Payment Status</th>
                        {/* <th>Actions</th> */}
                    </tr>
                </thead>
                <tbody>
                    {billings.length > 0 ? (
                        billings.map((billing) => (
                            <tr key={billing.id}>
                                <td>{billing.id}</td>
                                <td>{billing.patientName}</td>
                                <td>{new Date(billing.billingDate).toLocaleDateString()}</td>
                                <td>₹{billing.totalAmount.toFixed(2)}</td>
                                <td>{billing.paymentStatus}</td>
                                <td>
                                    <Link to={`/edit-invoice/${billing.id}`} className="btn">Edit</Link>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6" style={{ textAlign: 'center' }}>No invoices found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default BillingList;
