import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getAppointmentById, updateAppointment } from '../services/AppointmentService';

const EditAppointment = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    
    const [appointment, setAppointment] = useState({
        patient: { id: '' },
        doctor: { id: '' },
        appointmentDate: '',
        reason: '',
        status: ''
    });

    useEffect(() => {
        getAppointmentById(id).then((res) => setAppointment(res.data));
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAppointment({ ...appointment, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await updateAppointment(id, appointment);
        navigate('/appointments');
    };

    return (
        <div className="container">
            <h2>Edit Appointment</h2>
            <form onSubmit={handleSubmit}>
                <label>Reason:</label>
                <input type="text" name="reason" value={appointment.reason} onChange={handleChange} />
                <button type="submit">Save</button>
            </form>
        </div>
    );
};

export default EditAppointment;
