import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AppointmentList = () => {
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios.get('http://localhost:8080/api/appointments')
            .then(res => {
                setAppointments(res.data);
                setLoading(false);
            })
            .catch(err => {
                console.error('Error fetching appointments:', err);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="p-8 max-w-4xl mx-auto bg-white shadow-lg rounded-lg">
            <h2 className="text-2xl font-bold mb-4">Appointment List</h2>

            <table className="w-full border-collapse border border-gray-300">
                <thead>
                    <tr className="bg-gray-100">
                        <th className="p-3 border">Doctor</th>
                        <th className="p-3 border">Phone</th>
                        <th className="p-3 border">Email</th>
                        <th className="p-3 border">Reason</th>
                        <th className="p-3 border">Appointment Date</th>
                    </tr>
                </thead>
                <tbody>
                    {appointments.map((appointment) => (
                        <tr key={appointment.id} className="hover:bg-gray-50">
                            <td className="p-3 border">{appointment.doctor?.name || 'N/A'}</td>
                            <td className="p-3 border">{appointment.phone}</td>
                            <td className="p-3 border">{appointment.email}</td>
                            <td className="p-3 border">{appointment.reason}</td>
                            <td className="p-3 border">
                                {new Date(appointment.appointmentDate).toLocaleString()}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AppointmentList;
