import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AppointmentForm.css';

const AppointmentForm = () => {
    const navigate = useNavigate();
    
    const [formData, setFormData] = useState({
        doctorId: '',     // Ensure this matches backend field
        phone: '',
        email: '',
        reason: ''
    });

    const [doctors, setDoctors] = useState([]);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // Fetch doctors list on component mount
    useEffect(() => {
        axios.get('http://localhost:8080/api/doctors')
            .then(response => {
                setDoctors(response.data);
            })
            .catch(error => {
                console.error('Error fetching doctors:', error);
                setError('Failed to fetch doctors');
            });
    }, []);

    // Handle form field changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Validate and submit the form
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            // Log formData before making the request
            console.log('Submitting:', formData);
            
            const response = await axios.post('http://localhost:8080/api/appointments', formData);
            
            setSuccess('Appointment booked successfully!');
            setError('');
            navigate('/appointments');  // Redirect after successful booking
        } catch (error) {
            console.error('Failed to book appointment:', error);

            if (error.response) {
                setError(`Failed: ${error.response.data}`);
            } else {
                setError('Failed to book appointment. Please try again.');
            }
            setSuccess('');
        }
    };

    return (
        <div className="form-container">
            <h2>Book an Appointment</h2>

            {error && <p className="error-message">{error}</p>}
            {success && <p className="success-message">{success}</p>}

            <form onSubmit={handleSubmit}>
                <label>Doctor:</label>
                <select name="doctorId" value={formData.doctorId} onChange={handleChange} required>
                    <option value="">Select Doctor</option>
                    {doctors.map((doctor) => (
                        <option key={doctor.id} value={doctor.id}>
                            {doctor.name}
                        </option>
                    ))}
                </select>

                <label>Phone:</label>
                <input
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Enter 10-digit phone number"
                    required
                />

                <label>Email:</label>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Enter valid email"
                    required
                />

                <label>Reason:</label>
                <textarea
                    name="reason"
                    value={formData.reason}
                    onChange={handleChange}
                    placeholder="Enter reason for appointment"
                    required
                />

                <button type="submit">Book Appointment</button>
            </form>
        </div>
    );
};

export default AppointmentForm;
