import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { MedicalRecordService } from "../services/api";

const CreateMedicalRecord = () => {
    const { patientId } = useParams();
    const navigate = useNavigate();
    const [record, setRecord] = useState({
        diagnosis: "",
        treatment: "",
        date: "",
        notes: ""
    });
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecord({ ...record, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            await MedicalRecordService.create(patientId, record);
            alert("Medical record created successfully!");
            navigate(`/patients/${patientId}`);
        } catch (error) {
            console.error("Error creating medical record:", error);
            alert("Failed to create medical record.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container mx-auto p-6">
            <h2>Create Medical Record</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="diagnosis" placeholder="Diagnosis" value={record.diagnosis} onChange={handleChange} required />
                <textarea name="treatment" placeholder="Treatment" value={record.treatment} onChange={handleChange} required />
                <input type="date" name="date" value={record.date} onChange={handleChange} required />
                <textarea name="notes" placeholder="Additional Notes" value={record.notes} onChange={handleChange} />
                <button type="submit" disabled={loading}>{loading ? "Creating..." : "Create Record"}</button>
            </form>
        </div>
    );
};

export default CreateMedicalRecord;
